package JavaBasics;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//while loop
		int i=1;//initialization
		while(i<=10) {//conditional
			System.out.println(i);
			i=i+1;
		}
		//For loop
		for(int j=1;j<=10;j++) {
			System.out.println(j);
			
		}System.out.println("*************");
		
		for(int k=10;k>=-10;k--) {
			System.out.println(k);
		}
	}

}
